(function($){$.fn.resizeImages=function(options){var defaults={maxWidth:600,msgResized:'Esta imagen ha sido redimensionada. Has click aqu&iacute; para mostrarla en su tama&ntilde;o real.',msgNotResized:'Este es el tama&ntilde;o original de la imagen. Has click aqu&iacute; para ajustarla a la p&aacute;gina.'};var settings=$.extend(defaults,options);this.each(function(){if($(this).is('img')==false){return}if($.browser.msie){var $src=$(this).attr('src');$(this).attr('src','').attr('src',$src)}$(this).load(function(){if($(this).width()<=settings.maxWidth){return}$(this).wrap('<div class="resizedImageContainer" style="width: '+settings.maxWidth+'px;" />');$(this).width(settings.maxWidth);var $parent=$(this).parent();$parent.prepend('<div>'+settings.msgResized+'</div>');$parent.children('div').click(function(){var $container=$(this).parent();if($container.hasClass('notResized')){$container.width(settings.maxWidth);$container.children('img').width(settings.maxWidth);$container.children('div').html(settings.msgResized);$container.removeClass('notResized')}else{var width=$container.children('img').width('auto').width();$container.css('width',width);$container.children('div').html(settings.msgNotResized);$container.addClass('notResized')}})})})}})(jQuery);